<script>
	import Icon from '@svizzle/ui/src/icons/Icon.svelte';
	import CheckSquare from '@svizzle/ui/src/icons/feather/CheckSquare.svelte';
	import ChevronRight from '@svizzle/ui/src/icons/feather/ChevronRight.svelte';
	import MinusSquare from '@svizzle/ui/src/icons/feather/MinusSquare.svelte';
	import Square from '@svizzle/ui/src/icons/feather/Square.svelte';
	import {noop} from '@svizzle/utils';
	import {createEventDispatcher} from 'svelte';

	const dispatch = createEventDispatcher();

	export let title = null;
	export let items = [];
	export let focusedId = null;
	export let showFocusedItem = false;

	const focused = id => () => dispatch('focused', id);
	const toggled = id => () => dispatch('toggled', id);
	const selectedAll = () => dispatch('selectedAll');
	const deselectedAll = () => dispatch('deselectedAll');
</script>

<div
	class='RegionsSelector'
	on:click|stopPropagation={noop}
>
	<!-- title -->

	{#if title}
		<h2>{title}</h2>
	{/if}

	<ul>
		<!-- items -->

		{#each items as {id, name, status}}
			<li>
				<!-- checkbox -->

				<span
					class='checker clickable'
					on:click={toggled(id)}
				>
					{#if status === 1}
						<Icon
							glyph={CheckSquare}
							size={20}
						/>
					{:else if status === 0}
						<Icon
							glyph={Square}
							size={20}
						/>
					{:else if status === -1}
						<Icon
							glyph={MinusSquare}
							size={20}
						/>
					{/if}
				</span>

				<!-- item -->

				<span
					class='item'
					class:clickable={showFocusedItem}
					class:focused={id === focusedId}
					on:click={focused(id)}
				>
					{name}
				</span>

				<!-- focused item arrow -->

				{#if showFocusedItem}
					<span class='chevron'>
						{#if id === focusedId}
							<Icon
								glyph={ChevronRight}
								size={20}
							/>
						{/if}
					</span>
				{/if}
			</li>
		{/each}

		<!-- select all -->

		<li
			class='end sep clickable'
			on:click={selectedAll}
		>
			<span>Select all</span>
			<span class='checker'>
				<Icon
					glyph={CheckSquare}
					size={20}
					stroke='palegreen'
				/>
			</span>
		</li>

		<!-- deselect all -->

		<li
			class='end clickable'
			on:click={deselectedAll}
		>
			<span>Deselect all</span>
			<span class='checker'>
				<Icon
					glyph={Square}
					size={20}
					stroke='red'
				/>
			</span>
		</li>
	</ul>
</div>

<style>
	.RegionsSelector {
		cursor: auto;
		height: 100%;
		overflow-y: auto;
		width: 100%;
	}

	ul {
		width: 100%;
	}

	li {
		width: 100%;
		display: flex;
		align-items: center;
	}

	li.end {
		justify-content: flex-end;
	}
	li.sep {
		margin-top: 0.5rem;
	}

	li:hover {
		background-color: rgba(200,200,200,0.2);
	}

	.checker {
		margin-right: 0.5rem;
	}

	li.end .checker {
		margin: 0 0 0 0.5rem;
	}

	.focused {
		font-weight: bold;
	}

	.clickable {
		cursor: pointer;
	}
</style>
